mapper.delete:

delete from tt_group_user
where GROUP_ID = #{groupId,jdbcType=VARCHAR}
  and OPEN_ID = #{openId,jdbcType=VARCHAR}

mapper.select:

select GROUP_ID, OPEN_ID, NICKNAME, STATUS, CREATE_BY, CREATE_ON, UPDATE_BY, UPDATE_ON
from tt_group_user
where GROUP_ID = #{groupId,jdbcType=VARCHAR}
  and OPEN_ID = #{openId,jdbcType=VARCHAR}
