mapper.insert:

insert into tt_news (media_id, title, digest, 
  url, created_on, updated_on, 
  type, thumb_url, content
  )
values (#{mediaId,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, #{digest,jdbcType=VARCHAR}, 
  #{url,jdbcType=VARCHAR}, #{createdOn,jdbcType=TIMESTAMP}, #{updatedOn,jdbcType=TIMESTAMP}, 
  #{type,jdbcType=SMALLINT}, #{thumbUrl,jdbcType=VARCHAR}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.select:

select media_id, title, digest, url, created_on, updated_on, type, thumb_url, content
from tt_news
where media_id = #{mediaId,jdbcType=VARCHAR}
